def test_find_active_firmware():
    from krux.firmware import find_active_firmware
    for test_sector_case in TEST_SECTOR_CASES:
        address, size, i = find_active_firmware(test_sector_case[1])
        assert address == test_sector_case[0][0]
        assert size == test_sector_case[0][1]
        assert i == test_sector_case[0][2]

def test_update_boot_config_sector():
    from krux.firmware import update_boot_config_sector
    before_sector = SECTOR_WITH_NO_ACTIVE_FIRMWARE
    for i in range(8):
        after_sector = update_boot_config_sector(before_sector, i, FIRMWARE_SLOT_2, 1653985)
        assert after_sector[i*32:i*32+4] != before_sector[i*32:i*32+4]
        assert after_sector[i*32+4:i*32+4+4] != before_sector[i*32+4:i*32+4+4]
        assert after_sector[i*32+8:i*32+8+4] != before_sector[i*32+8:i*32+8+4]
        assert after_sector[i*32:i*32+4] == (0x5AA5D0C0 | 0b1101).to_bytes(4, 'big')
        assert after_sector[i*32+4:i*32+4+4] == FIRMWARE_SLOT_2.to_bytes(4, 'big')
        assert after_sector[i*32+8:i*32+8+4] == (1653985).to_bytes(4, 'big')

def test_fsize():
    from krux.firmware import fsize
    expected_size = len(open(TEST_FIRMWARE_FILENAME, 'rb').read())
    assert fsize(TEST_FIRMWARE_FILENAME) == expected_size

def test_sha256():
    from krux.firmware import sha256
    firmware = open(TEST_FIRMWARE_FILENAME, 'rb').read()
    size = len(firmware)
    expected_hash = hashlib.sha256(b'\x00' + size.to_bytes(4, 'little') + firmware)
    assert sha256(TEST_FIRMWARE_FILENAME, size) == expected_hash.digest()
    
def test_write_data_with_header_and_sha_suffix(mocker):
    mocker.patch('krux.firmware.flash', new=mock.MagicMock())
    import krux
    from krux.firmware import write_data

    num_callbacks = 0
    def percent_callback(pct):
        nonlocal num_callbacks
        num_callbacks += 1

    data = bytes(SECTOR_WITH_NO_ACTIVE_FIRMWARE)
    size = len(data)
    header = b'\x00' + size.to_bytes(4, 'little')
    hash = hashlib.sha256(header + data).digest()

    write_data(percent_callback, FIRMWARE_SLOT_1, io.BytesIO(data), size, 1024, header=True, sha_suffix=hash)

    assert num_callbacks == 6
    krux.firmware.flash.erase.assert_has_calls([
        mock.call(FIRMWARE_SLOT_1, 1024),
        mock.call(FIRMWARE_SLOT_1 + 1024, 1024),
        mock.call(FIRMWARE_SLOT_1 + 2048, 1024),
        mock.call(FIRMWARE_SLOT_1 + 3072, 1024),
        mock.call(FIRMWARE_SLOT_1 + 4096, 1024)
    ])
    krux.firmware.flash.write.assert_has_calls([
        mock.call(FIRMWARE_SLOT_1, header),
        mock.call(FIRMWARE_SLOT_1 + 5, data[:1024-5]),
        mock.call(FIRMWARE_SLOT_1 + 1024, data[1024-5:1024-5+1024]),
        mock.call(FIRMWARE_SLOT_1 + 2048, data[1024-5+1024:1024-5+2*1024]),
        mock.call(FIRMWARE_SLOT_1 + 3072, data[1024-5+2*1024:1024-5+3*1024]),
        mock.call(FIRMWARE_SLOT_1 + 4096, data[1024-5+3*1024:] + hash + (b'\x00' * (1024 - 5 - len(hash))))
    ])

